DROP TABLE IF EXISTS `role`;
CREATE TABLE `role` (
  `role_id` int PRIMARY key COMMENT '角色编号',
  `name` VARCHAR(64) not NULL COMMENT '角色名字',
  `description` VARCHAR(20) not null COMMENT '角色描述'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='角色表';
