-- ----------------------------
-- Table structure for `admin`
-- ----------------------------
DROP TABLE IF EXISTS `admin`;
CREATE TABLE `admin` (
  `uname` VARCHAR(20) PRIMARY key COMMENT '用户名',
  `upwd` VARCHAR(64) not NULL COMMENT '密码',
  `real_name` VARCHAR(20) not null COMMENT '真实姓名',
  `sex` int(1) DEFAULT 1 COMMENT '性别,1代表男，0代表女',
  `email` VARCHAR(20) default null COMMENT '邮箱',
  `status` int(1) DEFAULT 1 COMMENT '状态,1代表正常，0代表禁用',
  `role_id` int DEFAULT '1' COMMENT '角色编号',
  `picurl` VARCHAR(64) default '/img/profilePic/default_admin.png' COMMENT '用户头像地址'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='管理员用户表';
