-- ----------------------------
-- Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `uname` VARCHAR(20) PRIMARY key COMMENT '学号/教师编号',
  `upwd` VARCHAR(64) not NULL COMMENT '密码',
  `real_name` VARCHAR(20) not NULL COMMENT '真实姓名',
  `sex` int(1) DEFAULT 1 COMMENT '性别,1代表男，0代表女',
  `mobile` VARCHAR(20) DEFAULT NULL COMMENT '联系方式',
  `status` int(1) DEFAULT 1 COMMENT '状态,1代表正常，0代表禁用',
  `role_id` int DEFAULT '0' COMMENT '角色编号',
  `picurl` VARCHAR(64) default '/img/profilePic/default.jpg' COMMENT '用户头像地址'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';
